import React from 'react';
import { useProducts } from '../../context/ProductContext';
import { Link } from 'react-router-dom';

const FeaturedCategories: React.FC = () => {
  const { categories } = useProducts();

  return (
    <section className="py-10">
      <h2 className="text-2xl font-bold mb-6 text-center">Shop by Categories</h2>
      <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4 sm:gap-6 max-w-xs sm:max-w-2xl md:max-w-4xl lg:max-w-6xl mx-auto">
        {categories.map((cat) => (
          <Link
            key={cat.id}
            to={`/category/${cat.slug}`}
            className="flex flex-col items-center bg-white rounded-3xl shadow hover:shadow-lg p-3 sm:p-4 transition-all cursor-pointer hover:bg-orange-50 w-full"
          >
            <img
              src={cat.image}
              alt={cat.name}
              className="w-16 h-16 sm:w-20 sm:h-20 object-cover rounded-full mb-2 sm:mb-3 border-2 border-orange-400 bg-gray-50"
              onError={e => (e.currentTarget.src = '/default-category.png')}
            />
            <span className="font-semibold text-center text-xs sm:text-sm mt-1">{cat.name}</span>
          </Link>
        ))}
      </div>
    </section>
  );
};

export default FeaturedCategories; 