import React from 'react';
import { Link } from 'react-router-dom';
import { ArrowRight } from 'lucide-react';
import { useProducts } from '../context/ProductContext';

const Categories: React.FC = () => {
  const { categories, getProductsByCategory } = useProducts();

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="text-center mb-12">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Shop by Category</h1>
          <p className="text-lg text-gray-600">Find exactly what you're looking for</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {categories.map((category) => (
            <div key={category.id} className="bg-white rounded-lg shadow-md overflow-hidden">
              <div className="aspect-video bg-gradient-to-br from-primary to-orange-400 flex items-center justify-center">
                <h2 className="text-2xl font-bold text-white">{category.name}</h2>
              </div>
              
              <div className="p-6">
                <p className="text-gray-600 mb-4">{category.description}</p>
                <p className="text-sm text-gray-500 mb-6">
                  {getProductsByCategory(category.slug).length} products available
                </p>
                
                {category.subcategories.length > 0 && (
                  <div className="mb-6">
                    <h3 className="font-semibold text-gray-900 mb-3">Subcategories:</h3>
                    <div className="grid grid-cols-1 gap-2">
                      {category.subcategories.map((subcategory) => (
                        <Link
                          key={subcategory.id}
                          to={`/category/${category.slug}/${subcategory.slug}`}
                          className="text-primary hover:text-primary-dark text-sm flex items-center justify-between p-2 rounded hover:bg-primary-light transition-colors"
                        >
                          <span>{subcategory.name}</span>
                          <span className="text-gray-500">
                            {getProductsByCategory(category.slug, subcategory.slug).length}
                          </span>
                        </Link>
                      ))}
                    </div>
                  </div>
                )}
                
                <Link
                  to={`/category/${category.slug}`}
                  className="inline-flex items-center space-x-2 bg-primary text-white px-4 py-2 rounded-lg hover:bg-primary-dark transition-colors"
                >
                  <span>View All</span>
                  <ArrowRight className="w-4 h-4" />
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Categories;